import requestsimport streamlit as stimport pandas as pdimport streamlit as st# HTML con dise√±o de columnas y una tablahtml_layout = """    <div style="display: flex; justify-content: space-between;">        <div style="width: 45%;">            <h2>Columna 1</h2>            <p>Contenido de la primera columna.</p>        </div>        <div style="width: 45%;">            <h2>Columna 2</h2>            <p>Contenido de la segunda columna.</p>        </div>    </div>    <table style="width: 100%; margin-top: 20px; border-collapse: collapse;">        <tr>            <th style="border: 1px solid black; padding: 8px;">Columna A</th>            <th style="border: 1px solid black; padding: 8px;">Columna B</th>        </tr>        <tr>            <td style="border: 1px solid black; padding: 8px;">Dato 1</td>            <td style="border: 1px solid black; padding: 8px;">Dato 2</td>        </tr>    </table>"""st.components.v1.html(html_layout, height=400)# Display the logo from a URLlogo_url = "https://i1.sndcdn.com/avatars-TUVYyVNGNRk1TF07-p27gng-t500x500.jpg"st.image(logo_url, width=200)  # Adjust width as needed# Fetch data from the APIdata = requests.get("https://iph5309hnj.execute-api.us-east-1.amazonaws.com/dev/search-observations").json()st.title("Observaciones")# Extract only the "event" field from each noticeevents = [    {"event": notice["event"], "id": notice["id"], "observation": notice["observation"], "status": notice["status"]}    for notice in data["notices"]]# Convert the extracted events into a DataFramedf = pd.DataFrame(events)# Rename the "event" column to "aviso"df.rename(columns={"event": "Aviso","observation": "Observaciones",}, inplace=True)# Display the events in a tablest.table(df)